import React from 'react' ;
import { View , Text , Image ,TouchableOpacity} from "react-native";
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons' ;

const localRestaurants = [{
  name : "Agrawal Caterars",
  image_url : "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQw4Xh_bLhh6djP_iQ-T2riarCa6VhmMSlCDg&usqp=CAU",
  categories : [Bakery , Mithai],
  price : " ₹ 200" ,
  reviews : 1430,
  ratings : 4.5 ,
},
{
  name : "DMB",
  image_url : "https://lh3.googleusercontent.com/McW_IZUAlS6eAU39VO8pbQClrLf---BF34W-jyVfUiYlwL8BgzvqdXWj2vkH859z4FQKGpBH_zAWPKQDjZclI5axc9s5=w512",
  categories : [Restaurant , Outlet],
  price : " ₹ 220" ,
  reviews : 1390,
  ratings : 4.4 ,
},
{
  name : "NBC",
  image_url : "https://lh3.googleusercontent.com/McW_IZUAlS6eAU39VO8pbQClrLf---BF34W-jyVfUiYlwL8BgzvqdXWj2vkH859z4FQKGpBH_zAWPKQDjZclI5axc9s5=w512",
  categories : [Coffee , Cafe],
  price : " ₹ 400" ,
  reviews : 1575,
  ratings : 4.7 ,
},
]



export default function RestaurantItem() {
  return (
    <TouchableOpacity activeOpacity={1} style={{marginBottom : 30}}>
    <View  style = {{marginTop : 10 , padding : 15, backgroundColor : "white"}}>
     <RestaurantImage/>
     <RestaurantInfo/>
   </View>
    </TouchableOpacity>
  );
}


const RestaurantImage = () => (
  <>
      <Image 
      source = {{
        uri : "https://images.pexels.com/photos/1267320/pexels-photo-1267320.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
      }}
      style ={{ width : "100%" , height : 200}}
      // source = {require('../assets/images/rest.jpg')} 
      />
    <TouchableOpacity style={{position : "absolute", right : 20, top : 20}}>
      <MaterialCommunityIcons name="heart-outline" size={25} color="white"/>
    </TouchableOpacity>
  </>
);

const RestaurantInfo = () => (
  <View style={{flexDirection : "row", justifyContent : "space-between", alignItems : "center", marginTop : 10}}>
  <View>
  <Text style={{fontSize : 15, fontWeight : 'bold'}}>Farmhouse Kitchen Thai Cuisine</Text>
  <Text style={{fontSize : 13, color : "green"}}>30-45  Min</Text>
  </View>
  <View  style=
  {{ backgroundColor : "#eee", height : 30, width : 30, alignItems : "center", borderRadius : 15, justifyContent : "center"}}>
  <Text style = {{ color : "blue" }}> 4.5</Text>
  </View>
  </View>
)
